var artDisplayTopMenu = (function ($) {
    return function (menuitems) {
        var subList1 = $("");
        var subList2 = $("");
        var subList3 = $("");

        for (var i = 0; i < menuitems.length; i++) {
            var itemname = menuitems[i][0];
            var itemlink = menuitems[i][1];
            if (itemlink == "http://./") itemlink = "#";

            if (itemname.charAt(0) != "-") {
                if (subList3.length != 0) {
                    $("li:last", subList2).append(subList3);
                    subList3 = $("");
                }
                if (subList2.length != 0) {
                    $("li:last", subList1).append(subList2);
                    subList2 = $("");
                }
                if (subList1.length != 0) {
                    $("ul.hy-hmenu li:last").append(subList1);
                    subList1 = $("");
                }
                if (window.location == itemlink)
                    $("ul.hy-hmenu").append('<li><a href="' + itemlink + '" class="active">' + itemname + '</a></li>');
                else
                    $("ul.hy-hmenu").append('<li><a href="' + itemlink + '">' + itemname + '</a></li>');
            } else {
                if (itemname.charAt(2) == "-") {
                    if (subList3.length == 0) subList3 = $("<ul></ul>");
                    subList3.append('<li><a href="' + itemlink + '">' + itemname.substr(3, itemname.length) + '</a></li>');
                } else if (itemname.charAt(1) == "-") {
                    if (subList3.length != 0) {
                        $("li:last", subList2).append(subList3);
                        subList3 = $("");
                    }
                    if (subList2.length == 0) subList2 = $("<ul></ul>");
                    subList2.append('<li><a href="' + itemlink + '">' + itemname.substr(2, itemname.length) + '</a></li>');
                } else if (itemname.charAt(0) == "-") {
                    if (subList3.length != 0) {
                        $("li:last", subList2).append(subList3);
                        subList3 = $("");
                    }
                    if (subList2.length != 0) {
                        $("li:last", subList1).append(subList2);
                        subList2 = $("");
                    }
                    if (subList1.length == 0) subList1 = $("<ul></ul>");
                    subList1.append('<li><a href="' + itemlink + '">' + itemname.substr(1, itemname.length) + '</a></li>');
                }
            }
        }
        if (subList3.length != 0) {
            $("li:last", subList2).append(subList3);
            subList3 = $("");
        }
        if (subList2.length != 0) {
            $("li:last", subList1).append(subList2);
            subList2 = $("");
        }
        if (subList1.length != 0) {
            $("ul.hy-hmenu li:last").append(subList1);
            subList1 = $("");
        }
    };
})(jQuery);

var artDisplayVMenu = (function ($) {
    return function (vmenuitems) {

        if ($("ul.hy-vmenu").children().length > 0) return true;

        var subList1 = $("");
        var subList2 = $("");
        var subList3 = $("");

        for (var i = 0; i < vmenuitems.length; i++) {
            var itemname = vmenuitems[i][0];
            var itemlink = vmenuitems[i][1];
            if (itemlink == "http://./") itemlink = "#";

            if (itemname.charAt(0) != "-") {
                if (subList3.length != 0) {
                    $("li:last", subList2).append(subList3);
                    subList3 = $("");
                }
                if (subList2.length != 0) {
                    $("li:last", subList1).append(subList2);
                    subList2 = $("");
                }
                if (subList1.length != 0) {
                    $("ul.hy-vmenu li:last").append(subList1);
                    subList1 = $("");
                }
                if (window.location == itemlink)
                    $("ul.hy-vmenu").append('<li><a href="' + itemlink + '" class="active">' + itemname + '</a></li>');
                else
                    $("ul.hy-vmenu").append('<li><a href="' + itemlink + '">' + itemname + '</a></li>');
            } else {
                if (itemname.charAt(2) == "-") {
                    if (subList3.length == 0) subList3 = $("<ul></ul>");
                    if (window.location == itemlink)
                        subList3.append('<li><a href="' + itemlink + '" class="active">' + itemname.substr(3, itemname.length) + '</a></li>');
                    else
                        subList3.append('<li><a href="' + itemlink + '">' + itemname.substr(3, itemname.length) + '</a></li>');
                } else if (itemname.charAt(1) == "-") {
                    if (subList3.length != 0) {
                        $("li:last", subList2).append(subList3);
                        subList3 = $("");
                    }
                    if (subList2.length == 0) subList2 = $("<ul></ul>");
                    if (window.location == itemlink)
                        subList2.append('<li><a href="' + itemlink + '" class="active">' + itemname.substr(2, itemname.length) + '</a></li>');
                    else
                        subList2.append('<li><a href="' + itemlink + '">' + itemname.substr(2, itemname.length) + '</a></li>');
                } else if (itemname.charAt(0) == "-") {
                    if (subList3.length != 0) {
                        $("li:last", subList2).append(subList3);
                        subList3 = $("");
                    }
                    if (subList2.length != 0) {
                        $("li:last", subList1).append(subList2);
                        subList2 = $("");
                    }
                    if (subList1.length == 0) subList1 = $("<ul></ul>");
                    if (window.location == itemlink)
                        subList1.append('<li><a href="' + itemlink + '" class="active">' + itemname.substr(1, itemname.length) + '</a></li>');
                    else
                        subList1.append('<li><a href="' + itemlink + '">' + itemname.substr(1, itemname.length) + '</a></li>');
                }
            }
        }
        if (subList3.length != 0) {
            $("li:last", subList2).append(subList3);
            subList3 = $("");
        }
        if (subList2.length != 0) {
            $("li:last", subList1).append(subList2);
            subList2 = $("");
        }
        if (subList1.length != 0) {
            $("ul.hy-vmenu li:last").append(subList1);
            subList1 = $("");
        }

        if (!false) {
            $(".hy-vmenublockheader").css("display", "none");
        }

        var a = $("ul.hy-vmenu a.active");
        a.parents('ul.hy-vmenu').find("ul, a").removeClass('active');
        a.parent().children('ul').addClass('active');
        a.parents('ul.hy-vmenu ul').addClass('active');
        a.parents('ul.hy-vmenu li').children('a').addClass('active');
    };
})(jQuery);

var artFixVMenuArchive = (function ($) {
    return function () {
        if ($("#hy-vmenu-archive").length > 0) {
            if (!false) {
                $(".hy-vmenublockheader").css("display", "none");
            }
            $("#hy-vmenu-archive a").each(function (i) {
                if (this.href.replace('#', '') == window.location.href.replace('#', '')) {
                    $(this).addClass("active");
                }
            });
            var a = $("#hy-vmenu-archive a.active");
            a.parents('#hy-vmenu-archive').find("ul, a").removeClass('active');
            a.parent().children('ul').addClass('active');
            a.parents('#hy-vmenu-archive ul').addClass('active');
            a.parents('#hy-vmenu-archive li').children('a').addClass('active');
        }
    };
})(jQuery);


jQuery(function ($) {
    $(".BlogList").each(function (i) {
        var bl_name = $(this).attr("id");
        $("#" + bl_name + "_show-all").click(function () {
            $("#" + bl_name + "_blogs li").each(function (i) {
                if ($(this).css("display") == "none") {
                    $(this).css("display", "block");
                    $(this).attr("class", bl_name + "_blogs_hidden");
                    $("#" + bl_name + "_show-all").css("display", "none");
                    $("#" + bl_name + "_show-n").css("display", "block");
                }
            });
        });
        $("#" + bl_name + "_show-n").click(function () {
            $("li." + bl_name + "_blogs_hidden").each(function (i) {
                $(this).css("display", "none");
                $(this).attr("class", "");
                $("#" + bl_name + "_show-all").css("display", "block");
                $("#" + bl_name + "_show-n").css("display", "none");
            });
        });
    });
});

