jQuery(function ($) {
    var header = $("header.hy-header");
    var offset = header.offset();
    if (!offset) {
        return;
    }
    if ($("#navbar").length) {
        offset.top -= 30;
    }
    $("#hy-header-bg").css({
        "top": offset.top + "px"
    });
});

