    var menu = $(".hy-nav:not(#hy-hmenu-bg)");
    var offset = menu.offset();
    if (!offset) {
        return;
    }
    if ($("#navbar").length) {
        offset.top -= 30;
    }
    $("#hy-hmenu-bg").css({
        "height": menu.height() + "px",
        "top": offset.top + "px"
    });
});

